	private static final JSONArray jsonArray = new JSONArray(jsonStr);
	private static final  String emojiPicPath = "/res/plugins/emoji/emoji/";

	public static String replace(String content) {
		for (int i = 0; i < 5; i++) {
			JSONObject json = jsonArray.getJSONObject(i);
			JSONArray jsonArray = json.getJSONArray("items");
			for (int j = 0; j < jsonArray.length(); j++) {
				try {
					JSONObject jsona = jsonArray.getJSONObject(j);
					if(content.indexOf(":"+jsona.get("name")+":") > -1){
						content = content.replace(":"+jsona.get("name")+":","<div class='emoji' style=\"background-image:url('"+ Const.PROJECT_PATH + emojiPicPath+jsona.getString("src")+"');\"></div>");
					}
				} catch (Exception e) {
					continue;
				}
			}
		}
		return content;
	}



	/**
	 * 获取Emoji JSON数据
	 * @return
	 */
	public static String emojiJson(){
		JSONObject json = new JSONObject();
		json.put("data",jsonArray);
		json.put("path",emojiPicPath);
		return json.toString();
	}

